import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CustomModal from './Modal';
import Form from './Form';
import { Button, TextField, Typography } from '@material-ui/core';
import Axios from 'axios';

const useStyles = makeStyles((theme) => ({
  form: {
    backgroundColor: '#fff',
  }
}));

export default function AddTest({ open, handleClose }) {
  const classes = useStyles();
  const dateObj = new Date()
  var month = dateObj.getUTCMonth() + 1;
  var day = dateObj.getUTCDate();
  var year = dateObj.getUTCFullYear();
  const newDate = year + "-" + month + "-" + day;
  const [name, setName] = useState('')
  const [subject, setSubject] = useState('')
  const [date, setDate] = useState(newDate)
  const [marks, setMarks] = useState(0)

  const addPayment = () => {
    let payload = {
      id: window.localStorage.getItem('studentId'),
      name: name,
      subject: subject,
      date: date,
      marks: marks
    }
    Axios.post('http://localhost:5000/harsha/addPayment', payload)
      .then(res => {
        console.log('Test Added')
        handleClose()
      })
      .catch((error) => console.log(error.message))
  }

  return (
    <CustomModal open={open} handleClose={handleClose} >
      <Form className={classes.form} onSubmit={addPayment} >
        <Typography variant='h4' align='center' >Add Test</Typography>
        <TextField label='Name' value={name} name="name" onChange={(e) => { setName(e.target.value) }} type='text' placeholder='Enter Test name here!' variant='outlined' required />
        <TextField label='subject' value={subject} name="subject" onChange={(e) => { setSubject(e.target.value) }} type='text' placeholder='Enter Test subject here!' variant='outlined' required />
        <TextField label='marks' value={marks} name="marks" onChange={(e) => { setMarks(e.target.value) }} type='number' min={1} max={100} placeholder='Enter student marks here!' variant='outlined' required />
        <TextField label='date' value={date} name="date" onChange={(e) => { setDate(e.target.value) }} type='date' placeholder='Enter test date!' variant='outlined' required />
        <Button variant='contained' style={{background:'#FFA726', color:'white'}} type='submit'>Add</Button>
        <Button variant='contained' color='default' onClick={handleClose}>Cancel</Button>
      </Form>
    </CustomModal>
  );
}
